import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/dctfweb.css';

const Dctfweb = () => {
  const hoje = new Date();
  const mesAtual = String(hoje.getMonth() + 1).padStart(2, '0');
  const anoAtual = hoje.getFullYear();
  const [competencia, setCompetencia] = useState(`${mesAtual}/${anoAtual}`);

  const [empresas, setEmpresas] = useState([]);
  const [selecionadas, setSelecionadas] = useState([]);
  const [executando, setExecutando] = useState(false);
  const [mensagem, setMensagem] = useState('');

  const [filtros, setFiltros] = useState({
    cod: '',
    razaosocial: '',
    operador: '',
    cnpj: '',
    situacao: '',
    pagamento: '',
    data_vencimento: '',
    valor: '',
    postado: '',
    data_geracao: '',
  });

  // Nova função para atualizar status de postado
  const atualizarStatusPostado = async (cnpj, novoStatus) => {
    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_BASE}/api/dctfweb/status-postado/`, 
        { 
          cnpj: cnpj, 
          postado: novoStatus 
        }
      );

      // Atualizar o estado local para refletir a mudança
      setEmpresas(empresas.map(emp => 
        emp.cnpj === cnpj 
          ? { ...emp, postado: res.data.postado }
          : emp
      ));

      // Mostrar mensagem de sucesso
      setMensagem(`Status de postado para ${cnpj} atualizado para ${res.data.postado}`);
    } catch (error) {
      console.error('Erro ao atualizar status:', error);
      setMensagem(`Erro ao atualizar status: ${error.response?.data?.mensagem || error.message}`);
    }
  };

  useEffect(() => {
    if (!competencia) return;

    axios
      .get(`${process.env.REACT_APP_API_BASE}/api/empresas-dctfweb/?competencia=${competencia}`)
      .then((res) => setEmpresas(res.data))
      .catch(() => setMensagem('Erro ao carregar empresas.'));
  }, [competencia]);

  const handleCheckbox = (cnpj) => {
    setSelecionadas((prev) =>
      prev.includes(cnpj) ? prev.filter((item) => item !== cnpj) : [...prev, cnpj]
    );
  };

  // Restante do código anterior (baixarZip, executar, etc.) permanece o mesmo

  return (
    <div className="container mt-5 shadow p-4 rounded bg-white">
      <div className="header-wrapper">
        <i className="fas fa-file-alt header-icon"></i>
        <h2 className="titulo-pagina">Relatórios DCTFWeb</h2>
      </div>

      {mensagem && (
        <div className={`alert ${mensagem.includes('⚠️') || mensagem.includes('Erro') ? 'alert-danger' : 'alert-info'}`}>
          {mensagem}
        </div>
      )}

      {/* Resto do código de seleção de competência e botões permanece o mesmo */}

      <div className="table-responsive">
        <table className="table table-bordered table-hover text-center align-middle fs-6">
          <thead className="table-light">
            {/* Cabeçalhos da tabela */}
          </thead>
          <tbody>
            {empresas
              .filter(emp =>
                Object.entries(filtros).every(([campo, valor]) =>
                  emp[campo]?.toString().toLowerCase().includes(valor.toLowerCase())
                )
              )
              .map((empresa) => (
                <tr key={empresa.cnpj}>
                  <td>
                    <input
                      type="checkbox"
                      checked={selecionadas.includes(empresa.cnpj)}
                      onChange={() => handleCheckbox(empresa.cnpj)}
                    />
                  </td>
                  <td>{empresa.cod}</td>
                  <td className="text-start">{empresa.razaosocial}</td>
                  <td>{empresa.operador}</td>
                  <td>{empresa.cnpj}</td>
                  <td>
                    <span className={`badge ${empresa.situacao === 'Não gerado' ? 'bg-danger' : 'bg-success'}`}>
                      {empresa.situacao}
                    </span>
                  </td>
                  <td>
                    <span className={
                      empresa.pagamento === 'LANÇADO'
                        ? 'text-success fw-bold'
                        : empresa.pagamento
                          ? 'text-danger fw-bold'
                          : 'text-muted'
                    }>
                      {empresa.pagamento || '-'}
                    </span>
                  </td>
                  <td>{empresa.data_vencimento || '-'}</td>
                  <td>{empresa.valor}</td>
                  <td>
                    <div className="dropdown">
                      <button 
                        className={`btn btn-sm dropdown-toggle ${
                          empresa.postado === 'Sim' ? 'btn-success' : 'btn-danger'
                        }`} 
                        type="button" 
                        data-bs-toggle="dropdown"
                        aria-expanded="false"
                      >
                        {empresa.postado || 'Não'}
                      </button>
                      <ul className="dropdown-menu">
                        <li>
                          <button 
                            className="dropdown-item" 
                            onClick={() => atualizarStatusPostado(empresa.cnpj, true)}
                          >
                            Sim
                          </button>
                        </li>
                        <li>
                          <button 
                            className="dropdown-item" 
                            onClick={() => atualizarStatusPostado(empresa.cnpj, false)}
                          >
                            Não
                          </button>
                        </li>
                      </ul>
                    </div>
                  </td>
                  <td>{empresa.data_geracao || '-'}</td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dctfweb;
